const input = $input.first().json;
const text = input.text || '';

/**
 * Convert standard markdown to Slack mrkdwn format
 * 
 * Standard Markdown:          Slack mrkdwn:
 * **bold**                    *bold*
 * *italic*                    _italic_
 * ~~strikethrough~~           ~strikethrough~
 * [text](url)                 <url|text>
 * `code`                      `code` (same)
 * ```code block```            ```code block``` (same)
 * > blockquote                > blockquote (same)
 */
function toSlackMrkdwn(str) {
  if (!str) return '';
  let result = str;

  // Step 1: Protect code blocks (don't convert inside them)
  const codeBlocks = [];
  result = result.replace(/```[\s\S]*?```/g, (match) => {
    codeBlocks.push(match);
    return `\x00CODEBLOCK${codeBlocks.length - 1}\x00`;
  });

  // Step 2: Protect inline code
  const inlineCode = [];
  result = result.replace(/`[^`]+`/g, (match) => {
    inlineCode.push(match);
    return `\x00INLINE${inlineCode.length - 1}\x00`;
  });

  // Step 3: Convert strikethrough ~~text~~ to ~text~
  result = result.replace(/~~(.+?)~~/g, '~$1~');

  // Step 4: Convert bold **text** to placeholder (to protect from italic conversion)
  result = result.replace(/\*\*(.+?)\*\*/g, '\x00BOLD\x00$1\x00/BOLD\x00');

  // Step 5: Convert italic *text* to _text_ (only single asterisks now)
  // Use a simpler pattern that doesn't require lookbehind
  result = result.replace(/(?:^|[^*])\*([^*]+)\*(?:[^*]|$)/g, (match, content) => {
    const prefix = match[0] === '*' ? '' : match[0];
    const suffix = match[match.length - 1] === '*' ? '' : match[match.length - 1];
    return prefix + '_' + content + '_' + suffix;
  });

  // Step 6: Convert bold placeholder back to *text*
  result = result.replace(/\x00BOLD\x00(.+?)\x00\/BOLD\x00/g, '*$1*');

  // Step 7: Convert __text__ to *text* (alternative bold syntax)
  result = result.replace(/__(.+?)__/g, '*$1*');

  // Step 8: Convert [text](url) to <url|text>
  result = result.replace(/\[([^\]]+)\]\(([^)]+)\)/g, '<$2|$1>');

  // Step 9: Restore inline code
  result = result.replace(/\x00INLINE(\d+)\x00/g, (match, index) => inlineCode[parseInt(index)]);

  // Step 10: Restore code blocks
  result = result.replace(/\x00CODEBLOCK(\d+)\x00/g, (match, index) => codeBlocks[parseInt(index)]);

  return result;
}

const formatted = toSlackMrkdwn(text);

return [{
  json: {
    original: text,
    formatted: formatted
  }
}];

