const permissions = $input.first().json.effective_permissions;
const required = $input.first().json.required_permission;

// Check if permission exists and is true
const hasPermission = permissions && permissions[required] === true;

// Denial messages map
const denialMessages = {
  // Workflow permissions
  workflows_create: "Creating new automations requires admin privileges. I can describe what the workflow would look like, or I can notify an admin to create it for you.",
  workflows_activate: "Activating workflows requires admin privileges. Would you like me to notify an admin?",
  workflows_delete: "Deleting workflows requires admin privileges. Would you like me to notify an admin?",
  workflows_view: "Viewing workflows requires at least member access.",

  // Email permissions
  email_send: "I can draft emails for you, but sending requires member access. Would you like me to prepare a draft?",
  email_read: "Reading emails requires member access.",
  email_delete: "Deleting emails requires admin privileges for safety reasons.",

  // Calendar permissions
  calendar_write: "I can show you calendar availability, but creating or modifying events requires member access.",
  calendar_read: "Reading calendar data requires at least guest access.",

  // Document permissions
  docs_write: "I can read documents for you, but editing requires member access.",
  docs_create: "Creating documents requires member access.",
  docs_read: "Reading documents requires at least guest access.",

  // Sheets permissions
  sheets_write: "I can read spreadsheets for you, but editing requires member access.",
  sheets_read: "Reading spreadsheets requires at least guest access.",

  // Drive permissions
  drive_write: "I can browse files for you, but uploading or modifying requires member access.",
  drive_share: "Sharing files requires admin privileges.",
  drive_read: "Accessing Drive requires at least guest access.",

  // Task permissions
  tasks_write: "I can show you tasks, but creating or updating them requires member access.",
  tasks_read: "Reading tasks requires at least guest access.",

  // Admin permissions
  admin_manage_users: "User management requires admin privileges.",
  admin_view_all_data: "Viewing all user data requires admin privileges.",

  // Research
  research_web: "Web research requires at least guest access."
};

const denialMessage = hasPermission ? null : (denialMessages[required] || `This action requires the '${required}' permission.`);

return [{
  json: {
    permitted: hasPermission,
    required_permission: required,
    denial_message: denialMessage
  }
}];

