// Get the input slack_user_id and query result
const inputSlackUserId = $('Workflow Input').first().json.slack_user_id;
const queryResult = $input.all();

// Check if user was found (query returned a row with an id)
if (queryResult.length > 0 && queryResult[0].json.id) {
  const user = queryResult[0].json;
  return [{
    json: {
      found: true,
      is_admin: user.role === 'admin',
      user: {
        id: user.id,
        slack_user_id: user.slack_user_id,
        slack_username: user.slack_username,
        email: user.email,
        role: user.role,
        google_calendar_credential_id: user.google_calendar_credential_id,
        gmail_credential_id: user.gmail_credential_id,
        google_docs_credential_id: user.google_docs_credential_id,
        google_sheets_credential_id: user.google_sheets_credential_id,
        google_drive_credential_id: user.google_drive_credential_id,
        notion_credential_id: user.notion_credential_id,
        calendar_ids: user.calendar_ids,
        preferences: user.preferences,
        effective_permissions: user.effective_permissions
      }
    }
  }];
} else {
  return [{
    json: {
      found: false,
      error: 'User not registered or inactive',
      slack_user_id: inputSlackUserId
    }
  }];
}

