const message = $input.first().json;

const headers = message.payload?.headers || [];
const getHeader = (name) => headers.find(h => h.name.toLowerCase() === name.toLowerCase())?.value || '';

let body = '';
if (message.payload?.body?.data) {
  body = Buffer.from(message.payload.body.data, 'base64').toString('utf8');
} else if (message.payload?.parts) {
  const textPart = message.payload.parts.find(p => p.mimeType === 'text/plain');
  if (textPart?.body?.data) {
    body = Buffer.from(textPart.body.data, 'base64').toString('utf8');
  }
}

return [{
  json: {
    success: true,
    action: 'get_message',
    message_id: message.id,
    thread_id: message.threadId,
    from: getHeader('From'),
    to: getHeader('To'),
    subject: getHeader('Subject'),
    date: getHeader('Date'),
    snippet: message.snippet,
    body: body,
    labels: message.labelIds || []
  }
}];

const input = $input.first().json;
const routeData = $('Route by Action').first().json;

const to = routeData.to;
const subject = input.subject || routeData.subject;
const bodyPlain = input.body_plain || routeData.body;
const bodyHtml = input.body_html;
const cc = routeData.cc || '';
const bcc = routeData.bcc || '';
const accessToken = routeData.access_token;

const boundary = 'boundary_' + Date.now();

let mimeMessage = 'To: ' + to + '\n';
if (cc) mimeMessage += 'Cc: ' + cc + '\n';
if (bcc) mimeMessage += 'Bcc: ' + bcc + '\n';
mimeMessage += 'Subject: ' + subject + '\n';
mimeMessage += 'MIME-Version: 1.0\n';
mimeMessage += 'Content-Type: multipart/alternative; boundary="' + boundary + '"\n\n';
mimeMessage += '--' + boundary + '\n';
mimeMessage += 'Content-Type: text/plain; charset=utf-8\n\n';
mimeMessage += bodyPlain + '\n\n';
mimeMessage += '--' + boundary + '\n';
mimeMessage += 'Content-Type: text/html; charset=utf-8\n\n';
mimeMessage += bodyHtml + '\n\n';
mimeMessage += '--' + boundary + '--';

const encoded = Buffer.from(mimeMessage)
  .toString('base64')
  .replace(/\+/g, '-')
  .replace(/\//g, '_')
  .replace(/=+$/, '');

return [{
  json: {
    raw: encoded,
    access_token: accessToken
  }
}];

const routeData = $('Route by Action').first().json;
const formatResult = $('Format Email').first().json;
const userResult = $input.first().json;

return [{
  json: {
    action_type: 'email_send',
    user_id: userResult.id,
    slack_user_id: userResult.slack_user_id,
    slack_channel_id: routeData.channel_id || '',
    slack_thread_ts: routeData.thread_ts || '',
    payload: {
      to: routeData.to,
      cc: routeData.cc || '',
      bcc: routeData.bcc || '',
      subject: formatResult.subject || routeData.subject,
      body_plain: formatResult.body_plain || routeData.body,
      body_html: formatResult.body_html || '',
      user_email: routeData.user_email
    },
    expiry_minutes: parseInt(userResult.expiry_minutes) || 60
  }
}];

