const input = $('Workflow Input').item.json;
const items = $json.items || [];

// Parse time boundaries from input
const timeMin = input.time_min ? new Date(input.time_min) : null;
const timeMax = input.time_max ? new Date(input.time_max) : null;
const filterByStartTime = input.filter_starts_in_range === true || input.filter_starts_in_range === 'true';

const now = new Date();

// Helper to format duration
function formatDuration(ms) {
  const absMs = Math.abs(ms);
  const hours = Math.floor(absMs / 3600000);
  const minutes = Math.floor((absMs % 3600000) / 60000);
  if (hours > 0 && minutes > 0) return `${hours}h ${minutes}m`;
  if (hours > 0) return `${hours}h`;
  return `${minutes}m`;
}

// Helper to format time
function formatTime(date) {
  return date.toLocaleString('en-US', {
    hour: 'numeric',
    minute: '2-digit',
    hour12: true
  });
}

// Process each event with deterministic time evaluation
const processedEvents = items.map(event => {
  const startTime = event.start?.dateTime ? new Date(event.start.dateTime) : null;
  const endTime = event.end?.dateTime ? new Date(event.end.dateTime) : null;

  if (!startTime) {
    return { ...event, _status: 'unknown', _error: 'No start time' };
  }

  // Determine if event starts within the specified range
  let startsInRange = false;
  if (timeMin && timeMax) {
    startsInRange = startTime >= timeMin && startTime < timeMax;
  }

  // Determine event status relative to NOW
  let status, timeInfo = {};

  if (startTime > now) {
    status = 'upcoming';
    const msUntil = startTime - now;
    timeInfo = {
      _starts_in: formatDuration(msUntil),
      _starts_in_minutes: Math.round(msUntil / 60000),
      _start_time_formatted: formatTime(startTime)
    };
  } else if (endTime && endTime > now) {
    status = 'ongoing';
    const msElapsed = now - startTime;
    const msRemaining = endTime - now;
    timeInfo = {
      _time_elapsed: formatDuration(msElapsed),
      _time_remaining: formatDuration(msRemaining),
      _started_at: formatTime(startTime),
      _ends_at: formatTime(endTime)
    };
  } else {
    status = 'past';
    const msAgo = now - (endTime || startTime);
    timeInfo = {
      _ended_ago: formatDuration(msAgo),
      _ended_ago_minutes: Math.round(msAgo / 60000)
    };
  }

  return {
    ...event,
    _status: status,
    _starts_in_range: startsInRange,
    ...timeInfo
  };
});

// Apply filtering if requested
let filteredEvents = processedEvents;
if (filterByStartTime) {
  filteredEvents = processedEvents.filter(e => e._starts_in_range === true);
}

// Categorize events
const upcoming = filteredEvents.filter(e => e._status === 'upcoming');
const ongoing = filteredEvents.filter(e => e._status === 'ongoing');
const past = filteredEvents.filter(e => e._status === 'past');

return [{
  json: {
    success: true,
    action: 'list_events',
    filter_applied: filterByStartTime,
    query_window: timeMin && timeMax ? {
      time_min: timeMin.toISOString(),
      time_max: timeMax.toISOString()
    } : null,
    current_time: now.toISOString(),
    current_time_formatted: formatTime(now),
    summary: {
      total_returned: filteredEvents.length,
      total_before_filter: processedEvents.length,
      upcoming: upcoming.length,
      ongoing: ongoing.length,
      past: past.length
    },
    events: filteredEvents,
    events_by_status: {
      upcoming: upcoming,
      ongoing: ongoing,
      past: past
    }
  }
}];

const event = $input.first().json;
const input = $('Workflow Input').item.json;
const authData = $('Get Google Auth').item.json;

const userEmail = authData.user_email;
const rsvpStatus = input.rsvp_status || 'declined';

const validStatuses = ['accepted', 'declined', 'tentative', 'needsAction'];
if (!validStatuses.includes(rsvpStatus)) {
  return [{ json: { 
    success: false, 
    error: `Invalid RSVP status '${rsvpStatus}'. Valid values: ${validStatuses.join(', ')}` 
  }}];
}

let attendees = event.attendees || [];
let found = false;

for (let i = 0; i < attendees.length; i++) {
  if (attendees[i].self === true || 
      attendees[i].email?.toLowerCase() === userEmail?.toLowerCase()) {
    attendees[i].responseStatus = rsvpStatus;
    found = true;
    break;
  }
}

if (!found) {
  return [{ json: { 
    success: false, 
    error: `Could not find attendee entry for ${userEmail}. You may not be an attendee on this event.`,
    event_attendees: attendees.map(a => a.email)
  }}];
}

return [{ json: {
  attendees: attendees,
  event_id: input.event_id,
  calendar_id: input.calendar_id || 'primary',
  send_updates: input.send_updates || 'all',
  user_email: userEmail,
  new_status: rsvpStatus,
  event_summary: event.summary
}}];

