const doc = $input.first().json;

let text = '';
if (doc.body?.content) {
  for (const element of doc.body.content) {
    if (element.paragraph?.elements) {
      for (const textElement of element.paragraph.elements) {
        if (textElement.textRun?.content) {
          text += textElement.textRun.content;
        }
      }
    }
  }
}

return [{
  json: {
    success: true,
    action: 'get_document',
    document_id: doc.documentId,
    title: doc.title,
    text: text.trim(),
    revision_id: doc.revisionId,
    document_url: `https://docs.google.com/document/d/${doc.documentId}/edit`
  }
}];

const doc = $input.first().json;
const context = $('Merge Input Context').item.json;

// Find end index from document body
let endIndex = 1;
if (doc.body?.content) {
  const lastElement = doc.body.content[doc.body.content.length - 1];
  if (lastElement?.endIndex) {
    endIndex = lastElement.endIndex;
  }
}

const previousLength = endIndex - 1;
const newContent = context.content;

// Build requests array - delete existing content then insert new
const requests = [];

// Only delete if there's content to delete (endIndex > 2 means there's actual content)
if (endIndex > 2) {
  requests.push({
    deleteContentRange: {
      range: {
        startIndex: 1,
        endIndex: endIndex - 1
      }
    }
  });
}

// Insert new content
requests.push({
  insertText: {
    location: { index: 1 },
    text: newContent
  }
});

// Make the batchUpdate API call
const response = await this.helpers.httpRequest({
  method: 'POST',
  url: `https://docs.googleapis.com/v1/documents/${context.document_id}:batchUpdate`,
  headers: {
    'Authorization': `Bearer ${context.access_token}`,
    'Content-Type': 'application/json'
  },
  body: { requests }
});

return [{
  json: {
    success: true,
    action: 'update_document',
    document_id: context.document_id,
    document_url: `https://docs.google.com/document/d/${context.document_id}/edit`,
    previous_length: previousLength,
    new_length: newContent.length
  }
}];

const doc = $input.first().json;
const context = $('Merge Input Context').item.json;

const content = context.content;
const position = context.position || 'start';

// Determine insertion location
let insertRequest;
let insertedAt;

if (position === 'end') {
  // Use endOfSegmentLocation for end insertion
  insertRequest = {
    insertText: {
      endOfSegmentLocation: {},
      text: content
    }
  };
  insertedAt = 'end';
} else if (position === 'start') {
  // Insert at beginning (index 1)
  insertRequest = {
    insertText: {
      location: { index: 1 },
      text: content
    }
  };
  insertedAt = 1;
} else {
  // Numeric index
  const index = parseInt(position, 10);
  insertRequest = {
    insertText: {
      location: { index: index },
      text: content
    }
  };
  insertedAt = index;
}

// Make the batchUpdate API call
const response = await this.helpers.httpRequest({
  method: 'POST',
  url: `https://docs.googleapis.com/v1/documents/${context.document_id}:batchUpdate`,
  headers: {
    'Authorization': `Bearer ${context.access_token}`,
    'Content-Type': 'application/json'
  },
  body: { requests: [insertRequest] }
});

return [{
  json: {
    success: true,
    action: 'insert_text',
    document_id: context.document_id,
    inserted_at: insertedAt,
    character_count: content.length
  }
}];

