const input = $input.first().json;
const key = input.preference_key;
const value = input.preference_value;

// Define allowed root-level preferences and their types
const allowedPreferences = {
  'timezone': { type: 'string', description: 'IANA timezone (e.g., America/New_York)' },
  'personality': { type: 'enum', values: ['alfred', 'donna', 'jarvis', 'jim'], description: 'AI personality' },
  'verbosity': { type: 'enum', values: ['concise', 'detailed'], description: 'Response verbosity' },
  'proactive_notifications': { type: 'boolean', description: 'Allow proactive notifications' }
};

// Check if key is allowed
if (!allowedPreferences[key]) {
  const allowedKeys = Object.keys(allowedPreferences).join(', ');
  return [{
    json: {
      is_valid: false,
      error: `Invalid preference key '${key}'. Allowed keys: ${allowedKeys}. For daily_schedule settings, use the Daily Schedule Preferences tool.`
    }
  }];
}

const pref = allowedPreferences[key];
let parsedValue = value;
let jsonValue; // Pre-formatted JSON value for PostgreSQL

// Type validation and parsing
if (pref.type === 'boolean') {
  if (value === 'true' || value === true) {
    parsedValue = true;
    jsonValue = 'true'; // JSON boolean literal
  } else if (value === 'false' || value === false) {
    parsedValue = false;
    jsonValue = 'false'; // JSON boolean literal
  } else {
    return [{
      json: {
        is_valid: false,
        error: `Invalid value for '${key}'. Expected true or false.`
      }
    }];
  }
} else if (pref.type === 'enum') {
  if (!pref.values.includes(value)) {
    return [{
      json: {
        is_valid: false,
        error: `Invalid value '${value}' for '${key}'. Allowed values: ${pref.values.join(', ')}`
      }
    }];
  }
  parsedValue = value;
  // For strings/enums, wrap in quotes for valid JSON string
  jsonValue = JSON.stringify(value);
} else {
  parsedValue = value;
  // For strings, wrap in quotes for valid JSON string
  jsonValue = JSON.stringify(value);
}

return [{
  json: {
    is_valid: true,
    slack_user_id: input.slack_user_id,
    preference_key: key,
    preference_value: parsedValue,
    json_value: jsonValue // Pre-formatted for PostgreSQL jsonb
  }
}];

const input = $('Validate Input').first().json;
const result = $input.first().json;

if (result && result.slack_user_id) {
  return [{
    json: {
      success: true,
      message: `Successfully updated ${input.preference_key} to ${input.preference_value}`,
      updated_preferences: result.preferences
    }
  }];
} else {
  return [{
    json: {
      success: false,
      message: 'Failed to update preference. User may not exist.'
    }
  }];
}

