const queryResult = $json;
const slackUser = $('Fetch Slack User Info').item.json;
const userExists = queryResult.user_exists === true;

return [{
  json: {
    userExists: userExists,
    existingUser: userExists ? {
      id: queryResult.id,
      slack_user_id: queryResult.slack_user_id,
      slack_username: queryResult.slack_username,
      email: queryResult.email,
      role: queryResult.role
    } : null,
    slackUser: slackUser
  }
}];

const input = $('Workflow Input').item.json;
const slackUser = $json.slackUser;
const slackUsername = slackUser.name || slackUser.real_name?.toLowerCase().replace(/\s+/g, '') || 'unknown';
const email = slackUser.profile?.email || '';
const slackTimezone = slackUser.tz || 'UTC';
const timezone = input.timezone_override || slackTimezone;

let role = 'member';
if (input.role === 'admin') role = 'admin';
else if (input.role === 'guest') role = 'guest';

let googleAuthMethod = 'none';
let googleCalendarCredId = null;
let gmailCredId = null;
let googleDocsCredId = null;

if (email.endsWith('@aclarify.com')) {
  googleAuthMethod = 'service_account';
  googleCalendarCredId = 'yBNZEWLXitD9cSiU';
  gmailCredId = 'v2wjW0tm9a1TxnZP';
  googleDocsCredId = 'yBNZEWLXitD9cSiU';
}

const calendarIds = email ? [email] : [];
const preferences = {
  timezone: timezone,
  daily_briefing: true,
  briefing_time: '08:15',
  personality: 'alfred',
  verbosity: 'concise',
  proactive_notifications: true
};

return [{
  json: {
    slack_user_id: input.slack_user_id,
    slack_username: slackUsername,
    display_name: slackUser.real_name || slackUser.profile?.real_name || slackUsername,
    email: email,
    role: role,
    google_auth_method: googleAuthMethod,
    google_calendar_credential_id: googleCalendarCredId,
    gmail_credential_id: gmailCredId,
    google_docs_credential_id: googleDocsCredId,
    calendar_ids: calendarIds,
    preferences: preferences,
    slack_timezone: slackTimezone,
    timezone_used: timezone,
    timezone_was_overridden: !!input.timezone_override
  }
}];

const users = $input.all().map(item => item.json);
const formattedUsers = users.map(u => ({
  id: u.id,
  slack_user_id: u.slack_user_id,
  slack_username: u.slack_username,
  email: u.email,
  role: u.role,
  timezone: u.preferences?.timezone || 'Unknown',
  google_auth: u.google_auth_method || 'none',
  is_active: u.is_active
}));

return [{
  json: {
    success: true,
    action: 'list_users',
    total_users: users.length,
    users: formattedUsers
  }
}];

const input = $('Workflow Input').item.json;
const slackUser = $json;
const requestingUserId = input.requesting_user_id;
const targetUserId = input.slack_user_id;

const slackUsername = slackUser.name || slackUser.real_name?.toLowerCase().replace(/\s+/g, '') || 'unknown';
const email = slackUser.profile?.email || '';
const slackTimezone = slackUser.tz || 'UTC';
const timezone = input.timezone_override || slackTimezone;

let requestedRole = input.role || null;
let rbacError = null;

if (requestedRole) {
  const validRoles = ['admin', 'member', 'guest'];
  if (!validRoles.includes(requestedRole)) {
    rbacError = `Invalid role '${requestedRole}'. Valid roles: ${validRoles.join(', ')}`;
  }

  if (requestingUserId === targetUserId) {
    rbacError = 'RBAC violation: Administrators cannot change their own role. This prevents accidental lockout. Another admin must make this change.';
    requestedRole = null;
  }
}

let googleAuthMethod = 'none';
let googleCalendarCredId = null;
let gmailCredId = null;
let googleDocsCredId = null;

if (email.endsWith('@aclarify.com')) {
  googleAuthMethod = 'service_account';
  googleCalendarCredId = 'yBNZEWLXitD9cSiU';
  gmailCredId = 'v2wjW0tm9a1TxnZP';
  googleDocsCredId = 'yBNZEWLXitD9cSiU';
}

const calendarIds = email ? [email] : [];

return [{
  json: {
    rbac_error: rbacError,
    has_rbac_error: !!rbacError,
    slack_user_id: targetUserId,
    slack_username: slackUsername,
    display_name: slackUser.real_name || slackUser.profile?.real_name || slackUsername,
    email: email,
    role: requestedRole,
    google_auth_method: googleAuthMethod,
    google_calendar_credential_id: googleCalendarCredId,
    gmail_credential_id: gmailCredId,
    google_docs_credential_id: googleDocsCredId,
    calendar_ids: calendarIds,
    timezone: timezone,
    requesting_user_id: requestingUserId,
    role_change_attempted: !!input.role,
    role_change_blocked: !!rbacError
  }
}];

