const input = $input.first().json;

const query = input.query;
const context = input.context || '';
const allowedDomains = input.allowed_domains || '';

// Validate required fields
if (!query || query.trim() === '') {
  return [{
    json: {
      valid: false,
      error: 'Search query is required.',
      action: 'web_search'
    }
  }];
}

// Parse allowed domains if provided
let domainsList = [];
if (allowedDomains && allowedDomains.trim()) {
  domainsList = allowedDomains.split(',').map(d => d.trim()).filter(d => d.length > 0);
}

return [{
  json: {
    valid: true,
    query: query.trim(),
    context: context.trim(),
    allowed_domains: domainsList
  }
}];

const input = $input.first().json;

// Build the tool configuration
const webSearchTool = {
  type: 'web_search_preview'
};

// Add domain filtering if specified
if (input.allowed_domains && input.allowed_domains.length > 0) {
  webSearchTool.search_context_size = 'medium';
  webSearchTool.user_location = {
    type: 'approximate',
    country: 'US'
  };
}

// Build the user message with context if provided
let userMessage = input.query;
if (input.context && input.context.length > 0) {
  userMessage = `Context: ${input.context}\n\nSearch query: ${input.query}`;
}

// Build the request body
const requestBody = {
  model: 'gpt-4.1',
  tools: [webSearchTool],
  input: userMessage
};

return [{
  json: {
    requestBody: requestBody,
    query: input.query,
    context: input.context,
    allowed_domains: input.allowed_domains
  }
}];

const response = $input.first().json;
const body = response.body;
const originalQuery = $('Build API Request').first().json.query;

// Extract the output from the response
let outputText = '';
let sources = [];
let searchQueries = [];

try {
  // Parse the response - OpenAI Responses API format
  if (body && body.output) {
    // Get the text output
    for (const item of body.output) {
      if (item.type === 'message' && item.content) {
        for (const content of item.content) {
          if (content.type === 'output_text') {
            outputText = content.text || '';

            // Extract citations from annotations
            if (content.annotations) {
              for (const annotation of content.annotations) {
                if (annotation.type === 'url_citation') {
                  sources.push({
                    url: annotation.url,
                    title: annotation.title || annotation.url
                  });
                }
              }
            }
          }
        }
      }

      // Capture search queries used
      if (item.type === 'web_search_call') {
        searchQueries.push(item.query || originalQuery);
      }
    }
  }

  // Deduplicate sources by URL
  const uniqueSources = [];
  const seenUrls = new Set();
  for (const source of sources) {
    if (!seenUrls.has(source.url)) {
      seenUrls.add(source.url);
      uniqueSources.push(source);
    }
  }

  // Format sources for Slack mrkdwn
  let formattedResult = outputText;

  if (uniqueSources.length > 0) {
    formattedResult += '\n\n📚 *Sources:*';
    for (const source of uniqueSources) {
      formattedResult += `\n• <${source.url}|${source.title}>`;
    }
  }

  return [{
    json: {
      success: true,
      result: formattedResult,
      raw_text: outputText,
      sources: uniqueSources,
      search_queries: searchQueries.length > 0 ? searchQueries : [originalQuery],
      action: 'web_search',
      query: originalQuery
    }
  }];

} catch (error) {
  return [{
    json: {
      success: false,
      error: 'Failed to parse search results: ' + error.message,
      action: 'web_search',
      query: originalQuery
    }
  }];
}

const response = $input.first().json;
const originalQuery = $('Build API Request').first().json.query;

let errorMessage = 'Web search failed.';

// Handle specific error cases
const statusCode = response.statusCode || 0;
const body = response.body || {};

if (statusCode === 429) {
  errorMessage = 'Rate limit exceeded. Please try again in a moment.';
} else if (statusCode === 401 || statusCode === 403) {
  errorMessage = 'API authentication error. Please check configuration.';
} else if (statusCode >= 500) {
  errorMessage = 'Search service temporarily unavailable. Please try again.';
} else if (body.error && body.error.message) {
  errorMessage = body.error.message;
}

return [{
  json: {
    success: false,
    error: errorMessage,
    error_type: statusCode === 429 ? 'rate_limit' : statusCode >= 500 ? 'service_error' : 'api_error',
    action: 'web_search',
    query: originalQuery
  }
}];

